create table "StandardNarrations"
(
    "Id"          integer generated by default as identity
        constraint "StandardNarrations_PK"
            primary key,
    "VoucherType" integer      not null,
    "Narration"   varchar(100) not null
);

create table "AccountGroups"
(
    "Id"        integer generated by default as identity
        constraint "AccountGroups_PK"
            primary key,
    "GroupName" varchar(50) not null,
    "Alias"     varchar(50),
    "IsPrimary" boolean,
    "Parent"    integer
        constraint "AccountGroups_AccountGroups_Id_FK"
            references "AccountGroups"
);

create table "Accounts"
(
    "Id"                 integer generated by default as identity
        constraint "Accounts_PK"
            primary key,
    "AccountName"        varchar(100) not null,
    "Alias"              varchar(100),
    "PrintName"          varchar(100),
    "Group"              integer
        constraint "Accounts_AccountGroups_Id_FK"
            references "AccountGroups",
    "CreditLimit"        numeric(18, 2),
    "CreditDays"         integer,
    "Address"            varchar(500),
    "City"               varchar(100),
    "State"              varchar(100),
    "PinCode"            varchar(10),
    "Country"            varchar(100),
    "Phone"              varchar(20),
    "Mobile"             varchar(20),
    "Whatsapp"           varchar(20),
    "Email"              varchar(100),
    "ContactPerson"      varchar(100),
    "GSTIN"              varchar(20),
    "PAN"                varchar(20),
    "OpeningBalance"     numeric(18, 2),
    "OpeningBalanceType" varchar(1)
);

create table "ItemGroups"
(
    "Id"        integer generated by default as identity
        constraint "ItemGroups_PK"
            primary key,
    "GroupName" varchar(50) not null,
    "Alias"     varchar(50),
    "IsPrimary" boolean,
    "Parent"    integer
        constraint "ItemGroups_ItemGroups_Id_FK"
            references "ItemGroups"
);

create table "Items"
(
    "Id"       integer generated by default as identity
        constraint "Items_PK"
            primary key,
    "ItemName" varchar(100) not null,
    "Group"    integer
        constraint "Items_ItemGroups_Id_FK"
            references "ItemGroups"
);

create table "Units"
(
    "Id"        integer generated by default as identity
        constraint "Units_PK"
            primary key,
    "UnitName"  varchar(20) not null,
    "Alias"     varchar(20),
    "PrintName" varchar(20),
    "UQC"       varchar(3)
);

create table "UnitConversions"
(
    "Id"               integer generated by default as identity
        constraint "UnitConversions_PK"
            primary key,
    "MainUnit"         integer                    not null
        constraint "UnitConversions_MainUnits_Units_Id_FK"
            references "Units",
    "SubUnit"          integer                    not null
        constraint "UnitConversions_SubUnits_Units_Id_FK"
            references "Units",
    "ConversionFactor" double precision default 1 not null
);

